import React, { useState, useEffect } from "react";
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import axios from "axios";

function TaskCalendar() {

  const [events, setEvents] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:8080/tasks").then((response) => {
      const events = response.data.map((task) => {
        return {
          title: task.title,
          start: new Date(task.date),
          id: task.id,
          done: task.done,
        };
      });
      setEvents(events);
    });
  }, []);

  // const handleEventClick = (eventInfo) => {
  //   const taskId = eventInfo.event.id;
  //   const done = eventInfo.event.extendedProps.done;
  //   if (done === false) {
  //     axios.get(`http://localhost:8080/tasks/${taskId}`).then(() => {
  //       eventInfo.event.setExtendedProp("done", true);
  //       eventInfo.el.style.backgroundColor = "lightgreen";
  //     });
  //   }
  //   else{
  //     axios.get(`http://localhost:8080/tasks/${taskId}`).then(() => {
  //       eventInfo.event.setExtendedProp("done", false);
  //       eventInfo.el.style.backgroundColor = "red";
  //     });
  //   }
  // };

  return (
    <div className="container mx-auto my-8">
    <FullCalendar
      plugins={[dayGridPlugin, interactionPlugin]}
      initialView="dayGridMonth"
      events={events}
      // eventClick={handleEventClick}
    />
    </div>
  );
}
export default TaskCalendar;
