import React, { useEffect, useState } from 'react';
import axios from "axios";
import { Link, useNavigate, useParams } from 'react-router-dom';

export default function EditTask() {

    let navigate=useNavigate()

    const {id}=useParams()

    const [task, setTask]=useState({
        // id:"",
        date:"",
        title:"",
        details:"",
        priority:"",
        status:"",
    })

    const{date, title, details, priority, status} = task

    const onInputChange=(e)=>{

        setTask({...task,[e.target.name]:e.target.value});

    };

    useEffect(()=>{
        loadUser();
    }, []);

    const onSubmit =async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:8080/tasks/${id}`, task)
        navigate("/");

    };

    const loadUser = async () =>{
        const result=await axios.get(`http://localhost:8080/tasks/${id}`)
        setTask(result.data)
    }


  return (

    <div className="container">
        <div className="row">
            <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                <h2 className="text-center m-4">Edit task</h2>

                <form onSubmit={(e) => onSubmit(e)}>

                <div className="mb-3">
                    <label htmlFor="date" className="form-label"></label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a date"
                    name="date"
                    value={date}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="title" className="form-label"></label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a title"
                    name="title"
                    value={title}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="details" className="form-label"></label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a details"
                    name="details"
                    value={details}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="priority" className="form-label"></label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a priority"
                    name="priority"
                    value={priority}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="mb-4">
                    <label htmlFor="status" className="form-label"></label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a status"
                    name="status"
                    value={status}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="col-md-12 text-center">
                    <button type="submit" className="btn btn-primary">Save</button>
                    <Link className="btn btn-danger" to="/">Cancel</Link>
                </div>
                </form>

            </div>
        </div>
    </div>

  )
}
