import React, { useState } from 'react';
import axios from "axios";
import { Link, useNavigate } from 'react-router-dom';

export default function AddTask() {

    let navigate=useNavigate()

    const [task, setTask]=useState({
        date:"",
        title:"",
        details:"",
        priority:"",
        status:"",
    })

    const{date, title, details, priority, status} = task

    const onInputChange=(e)=>{

        setTask({...task,[e.target.name]:e.target.value});

    };

    const onSubmit =async (e) => {
        e.preventDefault();
        await axios.post("http://localhost:8080/api/create-task", task)
        navigate("/");

    };

  return (

    <div className="container">
        <div className="row">
            <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                <h2 className="text-center m-4">Add task</h2>

                <form onSubmit={(e) => onSubmit(e)}>

                <div className="mb-3">
                    <label htmlFor="date" className="form-label">Date</label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a date"
                    name="date"
                    value={date}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a title"
                    name="title"
                    value={title}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="details" className="form-label">Details</label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a details"
                    name="details"
                    value={details}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                {/* <div className="mb-3">
                    <label htmlFor="priority" className="form-label">Priority</label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a priority"
                    name="priority"
                    value={priority}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div> */}

<div className="dropdown">
  <a className="btn btn-secondary dropdown-toggle" href="#" role="button" id="dropdownMenuLink" data-bs-toggle="dropdown" aria-expanded="false">
    Dropdown link
  </a>

  <ul className="dropdown-menu" aria-labelledby="dropdownMenuLink">
    <li><a className="dropdown-item" href="#">Action</a></li>
    <li><a className="dropdown-item" href="#">Another action</a></li>
    <li><a className="dropdown-item" href="#">Something else here</a></li>
  </ul>
</div>

                <div className="mb-4">
                    <label htmlFor="status" className="form-label">Status</label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a status"
                    name="status"
                    value={status}
                    onChange={(e)=>onInputChange(e)}
                    />
                </div>

                <div className="col-md-12 text-center">
                    <button type="submit" className="btn btn-primary me-1">Save</button>
                    <Link className="btn btn-secondary me-1" to="/">Cancel</Link>
                </div>
                </form>

            </div>
        </div>
    </div>

  )
}
