import React, { useEffect, useState } from 'react';
import axios from "axios";
import { Link, useNavigate } from 'react-router-dom';

export default function Home() {

    const [tasks, setTasks] = useState([]);

    const {id}=useParams()

    useEffect(() => {
        loadTasks();

    }, []);

    const loadTasks = async () => {
        const result = await axios.get("http://localhost:8080/tasks");
        setTasks(result.data);
    }

    const deleteTask=async (id)=>{
        await axios.delete(`http://localhost:8080/tasks/${id}`)
        loadTasks();
    }

    return (
        <div className='container'>
            <div className='py-4'>
                <table className="table table-striped table-hover shadow">
                    <thead>
                        <tr>
                            <th scope="col"></th>
                            <th class="text-center">Date</th>
                            <th class="text-center">Title</th>
                            <th class="text-center">Details</th>
                            <th class="text-center">Priority</th>
                            <th class="text-center">Status</th>
                            <th class="text-center">Actions</th>
                        </tr>
                    </thead>
                    <tbody>

                        {
                            tasks.map((task,index)=>(
                        <tr>
                            <th scope="row" key={index}>{index+1}</th>
                            <td class="text-center">{task.date}</td>
                            <td class="text-center">{task.title}</td>
                            <td class="text-center">{task.details}</td>
                            <td class="text-center">{task.priority}</td>
                            <td class="text-center">{task.status}</td>
                            <td class="text-center">
                                <Link className="btn btn-primary mx-2" to={`/edit-task/${task.id}`}>Edit</Link>
                                <button className="btn btn-danger mx-2"
                                onClick={()=>deleteTask}>Delete</button>
                            </td>

                        </tr>

                        ))
                        }

                    </tbody>
                </table>
            </div>
        </div>
    )
}
