import React, { useState } from 'react';
import axios from "axios";
import { Link, useNavigate } from 'react-router-dom';
// import Select from "react-select";

export default function AddTask() {

    const TaskForm = ({onSave, task2 = {} }) => {
        const[task, setTask] = useState();
        const { date, title, details, priority, status } = task
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setTaskData((prevData) => ({...prevData, [name]: value }));
    };

    const handleSelectChange = (option) => {
        setTaskData((prevData) => ({ ...prevData, priority: option }));
    };

    const priorityOptions = [
        {value: "Urgnet", label: "Urgent"},
        {value: "Normal", label: "Normal"}
    ];




    // let navigate = useNavigate()

    // const [selectedOption, setSelectedOption] = useState();

    // const [task, setTask] = useState({
    //     date: "",
    //     title: "",
    //     details: "",
    //     priority: "",
    //     status: "False",
    // })







    return (

        <div className="container">
            <div className="row">
                <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                    <h2 className="text-center m-4">Add task</h2>

                        <div className="mb-3">
                            <label htmlFor="date" className="form-label">Date</label>
                            <input
                                type={"date"}
                                className="form-control"
                                placeholder="Enter a date"
                                name="date"
                                value={date}
                                onChange={handleChange}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="title" className="form-label">Title</label>
                            <input
                                type={"text"}
                                className="form-control"
                                placeholder="Enter a title"
                                name="title"
                                value={title}
                                onChange={handleChange}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="details" className="form-label">Details</label>
                            <input
                                type={"text"}
                                className="form-control"
                                placeholder="Enter a details"
                                name="details"
                                value={details}
                                onChange={handleChange}
                            />
                        </div>

                        <div className="dropdown">
                            Priority
                            <Select
                                value={priorityOptions.find(({value}) => value === priority)}
                                onChange={handleSelectChange}
                                options={priorityOptions}
                                name="priority"
                                className="form-control" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"
                            />
                        </div>

                        <br></br>

                        <div className="col-md-12 text-center">
                            <button type="submit" className="btn btn-primary me-1">Save</button>
                            <Link className="btn btn-secondary me-1" to="/">Cancel</Link>
                        </div>

                </div>
            </div>
        </div>

    )
}
