import React, { useState } from 'react';
import axios from "axios";
import { Link, useNavigate } from 'react-router-dom';

export default function AddTask() {

    let navigate=useNavigate()

    const [task, setTask]=useState({
        date:"",
        title:"",
        details:"",
        priority:"",
        status:"Done",
    })
    const [selectedOption, setSelectedOption] = useState('');

    const{date, title, details, priority, status} = task

    const handleSubmit = (event) => {
      event.preventDefault();
      axios.post("http://localhost:8080/api/create-task", task)
        navigate("/");
    };

    const inputChange = (event) => {
        setTask({...task,[`${event.target.name}`]:event.target.value});
        setSelectedOption(event.target.value);
    };

    // const handleSelectChange = (event) => {
    //   setSelectedOption(event.target.value);
    // };


  return (

    <div className="container">
        <div className="row">
            <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                <h2 className="text-center m-4">Add task</h2>

                <form onSubmit={handleSubmit}>

                <div className="mb-3">
                    <label htmlFor="date" className="form-label">Date</label>
                    <input
                    type="text"
                    className="form-control"
                    placeholder="Enter a date"
                    id="date"
                    name="date"
                    value={date}
                    onChange={inputChange}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a title"
                    name="title"
                    value={title}
                    onChange={inputChange}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="details" className="form-label">Details</label>
                    <input
                    type={"text"}
                    className="form-control"
                    placeholder="Enter a details"
                    name="details"
                    value={details}
                    onChange={inputChange}
                    />
                </div>



                <div className="dropdown">
                <label htmlFor="priority">Priority</label>
                <select id="priority" className="form-control" value={selectedOption} onChange={inputChange}>
                <option className="btn" value="">Select priority</option>
                <option value="NORMAL">Normal</option>
                <option value="URGNET">Urgent</option>
                </select>
                </div>

                <br></br>

                <div className="col-md-12 text-center">
                    <button type="submit" className="btn btn-primary me-1">Save</button>
                    <Link className="btn btn-secondary me-1" to="/">Cancel</Link>
                </div>
                </form>

            </div>
        </div>
    </div>

  )
}
